package classesandobjects;

public class SwapProgram {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=9;
		int b=8;
		int temp;
		System.out.println("Before swaping"+a+" "+b+" ");
        temp=a;
        a=b;
        b=temp;
        System.out.println("After swaping"+a+" "+b+" ");
		
	}

}
